"use client"

import { useState } from "react"
import { Phone, CheckCircle, Star } from "lucide-react"

interface PricingOption {
  id: number
  minutes: number
  price: number
  pricePerMinute: number
}

export default function PricingSelector() {
  const pricingOptions: PricingOption[] = [
    { id: 1, minutes: 60, price: 20, pricePerMinute: 16.6 },
    { id: 2, minutes: 45, price: 20, pricePerMinute: 18.8 },
    { id: 3, minutes: 30, price: 20, pricePerMinute: 23.3 },
    { id: 4, minutes: 15, price: 20, pricePerMinute: 33.2 },
  ]

  const [selectedOption, setSelectedOption] = useState<number>(1)

  return (
    <div className="max-w-7xl mx-auto px-4 py-16">
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 items-center">
        {/* Left side - Text and Stats */}
        <div className="space-y-12">
          <div>
            <h1 className="text-4xl md:text-5xl font-bold text-slate-600 mb-4">
              Pick the slot to get <span className="text-red-600">expert solution</span>
            </h1>
            <p className="text-2xl md:text-3xl text-slate-600 leading-relaxed">
              Right legal advice today <br />
              will save your big <br />
              cost tomorrow.
            </p>
          </div>

          <div className="space-y-6">
            <div className="flex items-center gap-4">
              <div className="w-12 h-12 rounded-full bg-amber-50 flex items-center justify-center">
                <Phone className="w-5 h-5 text-amber-500" />
              </div>
              <span className="text-xl text-slate-600">73k Calls Connected</span>
            </div>

            <div className="flex items-center gap-4">
              <div className="w-12 h-12 rounded-full bg-green-50 flex items-center justify-center">
                <CheckCircle className="w-5 h-5 text-green-500" />
              </div>
              <span className="text-xl text-slate-600">63k Positive Result</span>
            </div>

            <div className="flex items-center gap-4">
              <div className="w-12 h-12 rounded-full bg-pink-50 flex items-center justify-center">
                <Star className="w-5 h-5 text-pink-500" />
              </div>
              <span className="text-xl text-slate-600">4.6/5 Average Rating</span>
            </div>
          </div>
        </div>

        {/* Right side - Pricing Options */}
        <div className="space-y-4">
          {pricingOptions.map((option) => (
            <div
              key={option.id}
              className={`flex items-center justify-between p-6 rounded-xl border-2 ${
                selectedOption === option.id
                  ? "bg-purple-600 text-white border-purple-600"
                  : "bg-white text-slate-700 border-gray-200"
              } cursor-pointer transition-all duration-200 shadow-sm hover:shadow-md`}
              onClick={() => setSelectedOption(option.id)}
            >
              <div className="flex items-center gap-4">
                <div
                  className={`w-10 h-10 rounded-full border-2 ${
                    selectedOption === option.id ? "border-white" : "border-gray-300"
                  } flex items-center justify-center`}
                >
                  {selectedOption === option.id && <div className="w-6 h-6 rounded-full bg-white"></div>}
                </div>
                <div>
                  <h2 className="text-2xl font-bold">{option.minutes} Minutes</h2>
                  <p className={selectedOption === option.id ? "text-white/90" : "text-gray-500"}>
                    Minutes valid for 30 days
                  </p>
                </div>
              </div>
              <div className="text-right">
                <div className="text-4xl font-bold text-green-500">${option.price}</div>
                <div className={selectedOption === option.id ? "text-white/90" : "text-gray-500"}>
                  ( ${option.pricePerMinute.toFixed(1)}/ Min )
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  )
}

